import React from "react";
import axios from 'axios'; 

import Button from "react-bootstrap/Button";
import UploadService from "../services/upload-files.service";

export default class Upload extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            isFile:false, file:null, fileName:'', enableUploadButton: false

        };
    }


    handleUpload = (e) => {

        this.setState({isFile:true, file:e.target.files[0], fileName:e.target.files[0].name, enableUploadButton: true});

    };

    handleUploadButton = () =>{
        let currentFile = this.state.selectedFiles[0]; //

        this.setState({
          progress: 0,
          currentFile: currentFile,
        });

        UploadService.upload(currentFile, (event) => {
          this.setState({
            progress: Math.round((100 * event.loaded) / event.total),
          });
        })
          .then((response) => {
            this.setState({
              message: response.data.message,
            });
            return UploadService.getFiles();
          })
          .then((files) => {
            this.setState({
              fileInfos: files.data,
            });
          })
          .catch(() => {
            this.setState({
              progress: 0,
              message: "Could not upload the file!",
              currentFile: undefined,
            });
          });

        this.setState({
          selectedFiles: undefined,
        });
    }

    render() {
        return (
            <div id='uploadComponent'>
                <div className="d-flex justify-content-center">
                    <input
                        type="file"
                        accept="image/*,application/pdf"
                        className="mt-2 btn btn-dark w-75"
                        onChange={this.handleUpload}/>
                </div>
                <div className="col-xl-4 col-lg-4 col-md-12 mb-5 mt-5 col-sm-12 d-flex justify-content-center align-items-baseline">
                    <br />
                    {this.state.enableUploadButton ? 
                    (<button
                        type="button"
                        className=" btn btn-dark"
                    onClick={this.handleUploadButton}>Run OCR {this.state.fileName}</button>) : 
                        (<></>)}
                </div>
            </div>
        );
    }
}
